// imports para conexão 
import {useEffect, useState} from "react";
import axios from 'axios';



function Inicio(params) {
    
    const  [news, setNews] = useState([]);

    useEffect(()=> {
        const apiUrl = 'https://newsapi.org/v2/top-headlines?'
        const apiKey ='3ba42d5484964184aed69fefe75e86a9';
        const country = 'us'

        axios.get(`${apiUrl}?apiKey=${apiKey}country=${country}`)

        .then(response => {
            setNews(response.data.articles);
        })

        .catch(error => {
            console.error('Erro inesperado ao buscar noticias:', error);
        });

    }, []);
    

    return(

        /* minha intenção é fazer com que cada <div/> renderize uma noticia fornecida por uma api, no momento ainda não sei como fazer isso */
        
        <div className = 'container'>
            
            <h1> Principais Noticias </h1>
            <ul>
                {news.map((articles, index) => (
                    <li key={index}>
                        <h3>{articles.titulo}</h3>
                        <p>{articles.conteudo}</p>
                    </li>

                ))}
            
                    
        
            </ul>

            

        </div>
    );
    
};

export default Inicio
