// imports para conexão 
import {useEffect, useState} from "react";
import axios from 'axios';

// import de estilos
import styles from './Inicio.module.css'


function Inicio(params) {
    
    // conexão com a api de noticias do google

    const  [news, setNews] = useState([]);

    useEffect(()=> {
        const apiUrl ='https://newsapi.org/v2/top-headlines?'
        const apiKey ='3ba42d5484964184aed69fefe75e86a9';
        const country = 'br'
        const category = 'general'
        const language = 'en'
        
        axios.get(`${apiUrl}?country=${country}&category=${category}&language=${language}&apiKey=${apiKey}`)

        .then(response => {
            setNews(response.data.articles);
        })

        .catch(error => {
        
            console.error('Erro inesperado ao buscar noticias:', error);
        });

    }, []);
    
    const slides = news.map((article, index) => (
  <CarouselItem
    onExiting={() => setAnimating(true)}
    onExited={() => setAnimating(false)}
    key={index}
  >
    <Link to={`/detalhes/${index}`}>
      <h3>{article.title}</h3>
      <p dangerouslySetInnerHTML={{ __html: article.description }} />
      {/* Adicione mais informações conforme necessário */}
    </Link>
  </CarouselItem>
));

    return(
        
        <div className = {styles.conteiner}>

            
            
            <h1 className={styles.titulo}> Principais Noticias </h1>
            <ul className={styles.list}>
                {news.map((article, index) => (
                    <li className={styles.item} key={index}>
                        <h3>{article.title}</h3>
                        <p>{article.description}</p>
                        <p>{article.content}</p>
                        <p>{article.author}</p>
                        <p>{article.publishedAt}</p>
                        <p>Saiba mais</p>
                        <a href={article.url} target="blank">{article.url}</a>
                        
                    </li>

                ))}


            
                    
        
            </ul>

            

        </div>
    );
    
};

export default Inicio
