// imports para conexão 
import {useEffect, useState} from "react";
import axios from 'axios';

// import de estilos
impor


function Inicio(params) {
    
    // conexão com a api de noticias do google

    const  [news, setNews] = useState([]);

    useEffect(()=> {
        const apiUrl = 'https://newsapi.org/v2/top-headlines'
        const apiKey ='3ba42d5484964184aed69fefe75e86a9 ';
        const country = 'br'
        
        axios.get(`${apiUrl}?country=${country}&apiKey=${apiKey}`)

        .then(response => {
            setNews(response.data.articles);
        })

        .catch(error => {
            console.error('Erro inesperado ao buscar noticias:', error);
        });

    }, []);
    

    return(
        
        <div className = 'container'>
            
            <h1 className='titulo'> Principais Noticias </h1>
            <ul className="list">
                {news.map((articles, index) => (
                    <li className="item" key={index}>
                        <h3>{articles.titulo}</h3>
                        <p>{articles.conteudo}</p>
                    </li>

                ))}
            
                    
        
            </ul>

            

        </div>
    );
    
};

export default Inicio
