// imports para conexão 
import { useEffect, useState } from "react";
import axios from 'axios';
// imports de estilos
import { Carousel, CarouselItem, CarouselControl, CarouselIndicators} from 'reactstrap';
import styles from './Inicio.module.css';




function Inicio(params) {
    const [news, setNews] = useState([]);
    const [activeIndex, setActiveIndex] = useState(0);
    const [animating, setAnimating] = useState(false);

    useEffect(() => {
        const apiUrl = 'https://newsapi.org/v2/top-headlines?';
        const apiKey = '3ba42d5484964184aed69fefe75e86a9';
        const country = 'br';
        const category = 'general';
        const language = 'en';

        axios.get(`${apiUrl}?country=${country}&category=${category}&language=${language}&apiKey=${apiKey}`)
            .then(response => {
                setNews(response.data.articles);
            })
            .catch(error => {
                console.error('Erro inesperado ao buscar noticias:', error);
            });
    }, []);

    const next = () => {
        if (animating) return;
        const nextIndex = activeIndex === news.length - 1 ? 0 : activeIndex + 1;
        setActiveIndex(nextIndex);
    };

    const previous = () => {
        if (animating) return;
        const nextIndex = activeIndex === 0 ? news.length - 1 : activeIndex - 1;
        setActiveIndex(nextIndex);
    };

    const goToIndex = newIndex => {
        if (animating) return;
        setActiveIndex(newIndex);
    };

    const slides = news.map((article, index) => (
        <CarouselItem
            onExiting={() => setAnimating(true)}
            onExited={() => setAnimating(false)}
            key={index}
        >
            <h3>{article.title}</h3>
            <p>{article.description}</p>
            <p>{article.content}</p>
            <p>{article.author}</p>
            <a href={article.url} target="blank">S</a>
            {/* Adicione mais informações conforme necessário */}
        </CarouselItem>
    ));

    return (
        <div className={styles.conteiner}>
            <h1 className={styles.titulo}>Principais Noticias</h1>
            <Carousel
                activeIndex={activeIndex}
                next={next}
                previous={previous}
            >
                <CarouselIndicators items={news} activeIndex={activeIndex} onClickHandler={goToIndex} />
                {slides}
                <CarouselControl direction="prev" directionText="Previous" onClickHandler={previous} />
                <CarouselControl direction="next" directionText="Next" onClickHandler={next} />
            </Carousel>
        </div>
    );
}

export default Inicio;
